#include <stdio.h>

#define BIGINT_IMPL
#define BIGINT_USE_64_BIT
#include "bigInteger.h"

int main() {
  BigInt a, b, c;
  BigInt_init_from_string(&a, "");
  BigInt_init_from_string(&b, "");
  BigInt_init(&c);

  BigInt_add(&c, &a, &b);

  char * s = BigInt_to_string(&c);
  printf("Sum is %s\n", s);
  free(s);
  BigInt_print_internal(&c);

  BigInt_destroy(&a);
  BigInt_destroy(&b);
  BigInt_destroy(&c);
  return 0;
}
